// import constructors from './constructors';

// const URL = 'http://localhost:3000/admin/api'
const domain = 'http://localhost';
const port = '3000';
const route = 'admin/api';
const queryDocs = require('./constructors')
const GRAPHQL_URL = 'http://localhost:3000/admin/api'

const createCall = async (queryDoc) => {
  const payload = JSON.stringify({
    query: queryDoc,
  });
  console.log(payload)
  let response; 
  response = await fetch(
    GRAPHQL_URL,
    {
      method: 'POST',
      // headers: {
      //   Authorization: `token ${accessToken}`,
      // },
      body: payload,
    }
  );
  console.log(response)
  const body = await response.json();
  return {
    status: response.status,
    ok: response.ok,
    body: body.data,
  };
}

export const queryid = ({query}) => {
  console.log(queryDocs.queryAllUsers({query}))
  // createCall(queryDocs.queryAllUsers({query}))
}
