const fetch = require('isomorphic-unfetch')
const API = require('../api/index')
const api = require('../api/constructors')
const URL = 'http://localhost:3000/admin/api'
const queryDocs = require('../api/constructors')

function HomePage({stars}) {
return <div>{queryDocs.queryAllUsers}Welcome to Next.js!{stars}</div>
}

const query = `
query{
  allUsers{
    id
  }
}
`

HomePage.getInitialProps = async () => {
  // console.log(api.queryAllUsers('id'))
  const res = await fetch(URL, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({query})
  })
  // console.log(res)
  const body = await res.json()
  console.log(body.data)
  const d = body.data
  const data = JSON.stringify({ d })

  // const id = 'id'
  // const i = '1'
  // const res = API.queryid({id},i)
  // const d = res.data
  // const data = JSON.stringify({d})
  return { stars: data }
}

export default HomePage
