// const URL = 'http://localhost:3000/admin/api'

export default {
  DOMAIN: 'http://localhost',
  PORT: '3000',
  ROUTE: 'admin/api',
  URL: `${DOMAIN}:${PORT}/${ROUTE}`,
  queryAllUsers: ({ query }) => `
    query {
      allUsers {
        ${query}
      }
    }
  `
}
